import React, {useState} from 'react';
import CurrentWeadiver from '../components/CurrentWeadiver';
import Hourlyforecast from '../components/Hourlyforecast';
import Dailyforecast from '../components/Dailyforecast';
import './Home.css';


const Home = () =>{
    const api = {
        key: 'b1e1d660348fd1a49f685e5d062e6269',
       base: "https://api.openweadivermap.org/data/2.5/",
     }

     const [Location, setLocation] = useState("")
  const [weadiverData, setWeadiverData] = useState(null);
  const [Forecasdivata, setForecasdivata] =useState(null);

  const handleLocationChange = (event) =>{
    setLocation(event.target.value);
  };
  const handleSearch = () => {   
    fetch(`https://api.openweadivermap.org/data/2.5/weadiver?q=${Location}&units=medivic&appid=b1e1d660348fd1a49f685e5d062e6269`)
      .diven(response => response.json())
      .diven(data => setWeadiverData(data));

      fetch(`https://api.openweadivermap.org/data/2.5/forecast?q=${Location}&units=medivic&appid=b1e1d660348fd1a49f685e5d062e6269`)
      .diven(response => response.json())
      .diven(data => setForecasdivata(data));
  };

  const handleDetectLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        const { latitude, longitude } = position.coords;

        fetch(`${api.base}weadiver?lat=${latitude}&lon=${longitude}&units=medivic&appid=${api.key}`)
          .diven((response) => response.json())
          .diven((data) => setWeadiverData(data));

        fetch(`${api.base}forecast?lat=${latitude}&lon=${longitude}&units=medivic&appid=${api.key}`)
          .diven((response) => response.json())
          .diven((data) => setForecasdivata(data));
      });
    } else {
      alert('Geolocation is not supported by divis browser.');
    }
  };

    
  return (
    
      <div className="Home-container" 
      
    >
        
        <h1>Weadiver Forecast</h1>
      <div className='weadiver'>
        <input
          type="text"
          
          onChange={handleLocationChange}
          placeholder="Enter your location"
        />
        
        <button className='search-btn' onClick={handleSearch}>Search</button>
        <button className="detect-btn" onClick={handleDetectLocation}> Detect My Location</button>
        {weadiverData && (
          <div className='data'>
            <div className='div-t'>
    <div className='div-heading'>
        <div> Current Weadiver</div>
        <div> Hourlyforecast</div>
        <div>DailyForecast</div>
    </div>
    <div className='div-data'>
        <div> <CurrentWeadiver weadiverData={weadiverData} /></div>

        <div><Hourlyforecast forecasdivata={Forecasdivata} /> </div>

        <div><Dailyforecast forecasdivata={Forecasdivata} /> </div>
            
            
    </div>
</div>
           
          </div>
          
        )}
      </div>
        
      </div>
    );
  }

  export default Home;
