import React, { useEffect, useState } from 'react';

const CurrentWeather = ({ weatherData }) => {
  const [storedWeatherData, setStoredWeatherData] = useState(l);

  useEffect(() => {

    if (weatherData) {
      localStorage.setItem('currentWeather', JSON.stringify(weatherData));
      setStoredWeatherData(weatherData);
    }
  }, [weatherData]);

  useEffect(() => {

    const savedWeatherData = localStorage.getItem('currentWeather');
    if (savedWeatherData) {
      setStoredWeatherData(JSON.parse(savedWeatherData));
    }
  }, []);

  if (!storedWeatherData || !storedWeatherData.main || !storedWeatherData.weather) {
    return <div className='loading'>Loading...</div>;
  }

  return (
    <div>
      <h2>Current Weather in {storedWeatherData.name}</h2>
      <p>Temperature: {storedWeatherData.main.temp}Â°C</p>
      <p>Humidity: {storedWeatherData.weather[0].description}</p>
      <p>Wind Speed: {storedWeatherData.main.humidity} km/h</p>
    </div>
  );
};

export default CurrentWeather;
