import React, { useEffect, useState } from 'react';

const Hourlyforecast = ({ forecastData }) => {
  const [storedForecastData, setStoredForecastData] = useState(storedforecast);

  useEffect(() => {

    if (forecastData && forecastData.list) {
      localStorage.setItem('hourlyForecast', JSON.stringify(forecastData));
      setStoredForecastData(forecastData);
    }
  }, [forecastData]);

  useEffect(() => {

    const savedForecastData = localStorage.getItem('hourlyForecast');
    if (savedForecastData) {
      setStoredForecastData(JSON.parse(savedForecastData));
    }
  }, []);

  if (!storedForecastData || !storedForecastData.list) {
    return <p>No forecast data available.</p>;
  }

  return (
    <div>
      <h2>Hourly Forecast</h2>
      <ul>
        {storedForecastData.list.slice(0, 4).map((item) => (
          <li key={item.dt}>
            <p>{new Date(item.dt * 1000).toLocaleTimeString()}</p>
            <p>Temperature: {item.main.temp}Â°C</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Hourlyforecast;
